mapper.select:
select costNum, energyCost, consumeNum, TO_CHAR(energycostDate, 'YYYY-MM-DD HH24:MI:SS') as energycostDate from energycost where costNum is not null
and TO_CHAR(energycostDate, 'YYYY-MM') between #{fromDate} and #{toDate}
order by energycostDate desc

select count(*) from energycost where costNum is not null
and TO_CHAR(energycostDate, 'YYYY-MM') between #{fromDate} and #{toDate}
order by energycostDate desc

select TO_CHAR(energycostDate, 'YYYY') as year, sum(energyCost) as totalYear from energycost where costNum is not null
and TO_CHAR(energycostDate, 'YYYY') between #{fromDate} and #{toDate}
group by TO_CHAR(energycostDate, 'YYYY') order by year desc
